$('#subject').focus(function () {
    $('#subject').css('border-bottom','1px solid #000')
})

$('#title').focus(function () {
    $('#title').css('border-bottom','1px solid #000')
})

$('#email').focus(function () {
    $('#email').css('border-bottom','1px solid #000')
})

$('#subject').blur(function () {
    $('#subject').css('border-bottom','1px solid #ccc')
})

$('#title').blur(function () {
    $('#title').css('border-bottom','1px solid #ccc')
})

$('#email').blur(function () {
    $('#email').css('border-bottom','1px solid #ccc')
})
